package com.github.unidbg.ios;

import com.dd.plist.NSDictionary;
import com.dd.plist.PropertyListParser;
import com.github.unidbg.ios.struct.kernel.StatFS;
import com.github.unidbg.ios.struct.kernel.VmRegionBasicInfo;
import com.github.unidbg.ios.struct.kernel.VmRegionBasicInfo64;
import com.github.unidbg.ios.struct.sysctl.TaskVmInfo64;
import com.github.unidbg.utils.Inspector;
import junit.framework.TestCase;
import org.apache.commons.codec.binary.Base64;
import org.apache.commons.codec.binary.Hex;
import org.apache.commons.codec.digest.DigestUtils;

import java.io.File;
import java.util.Arrays;
import java.util.HashMap;
import java.util.Locale;
import java.util.Map;

public class PListTest extends TestCase {

    public void testStruct() throws Exception {
        VmRegionBasicInfo64 vmRegionBasicInfo64 = new VmRegionBasicInfo64(Hex.decodeHex("030000000300000001000000000000000000000000000000000000000000000000000000".toCharArray()));
        vmRegionBasicInfo64.unpack();
        System.out.println(vmRegionBasicInfo64);

        VmRegionBasicInfo vmRegionBasicInfo = new VmRegionBasicInfo(Hex.decodeHex("0100000001000000000000000000000000000000000000000000000000000000".toCharArray()));
        vmRegionBasicInfo.unpack();
        System.out.println(vmRegionBasicInfo);

        byte[] data = Hex.decodeHex("".toCharArray());
        TaskVmInfo64 vmInfo64 = new TaskVmInfo64(data);
        vmInfo64.unpack();
        System.out.println(vmInfo64);
    }

    public void testPList() throws Exception {
        Map<String, Object> map = new HashMap<>();
        map.put("AppleICUForce24HourTime", 1);
        map.put("AppleLanguages", new String[] { "zh-Hans", "en" });
        map.put("AppleLocale", "zh_CN");
        NSDictionary root = (NSDictionary) NSDictionary.fromJavaObject(map);
        PropertyListParser.saveAsBinary(root, new File("target/plist_test.plist"));
    }

    public void testLocale() {
        Locale locale = Locale.getDefault();
        System.out.println(locale);
        System.out.println(locale.getCountry());
        System.out.println(locale.getLanguage());
    }

    public void testHex() throws Exception {
        byte[] data = Hex.decodeHex("".toCharArray());
        for (int i = 0; i < 3; i++) {
            byte[] tmp = Arrays.copyOfRange(data, i * 2168, (i + 1) * 2168);
            Inspector.inspect(tmp, "statfs i=" + i + " base64=" + Base64.encodeBase64String(tmp));
        }
        System.out.println("md5=" + DigestUtils.md5Hex(data));
    }

    public void testBase64() {
        byte[] data = Base64.decodeBase64("");
        StatFS statFS = new StatFS(data);
        statFS.unpack();
        System.out.println(statFS);
        statFS.setMntOnName("/");
        statFS.pack();
        Inspector.inspect(data, "hex=" + Hex.encodeHexString(data));
    }

}
